import Head from "next/head";
import imageBuilder from "../image-builder";

export default function PhotoPosts(props) {
    const { posts } = props;
    const showImage = src => imageBuilder(src).url();

    return (
        <>
            <div className="mx-auto container pt-5">
                {posts.map(post => (
                    <img
                        className="lg:float-left h-64 w-full lg:w-64 m-2"
                        src={showImage(post.mainImage)}
                    />
                ))}
            </div>
        </>
    );
}
