import BlockContent from "@sanity/block-content-to-react";
import client from "./sanity-client";

export default function RenderBodyContent(body) {
    const serializers = {
        list: props => <div>{props.children}</div>,
        listItem: props => <strong>{props.children}</strong>,

        types: {
            image: props => (
                <figure className="has-text-centered">
                    <img
                        src={urlFor(props.node.asset).width(600).url()}
                        alt={props.node.alt}
                        style={{ borderRadius: "5px" }}
                    />

                    <figcaption>{props.node.caption}</figcaption>
                </figure>
            ),
            block(props) {
                switch (props.node.style) {
                    case "h1":
                        return <h1 className="">{props.children}</h1>;

                    case "h2":
                        return <h2 className="">{props.children}</h2>;

                    case "h3":
                        return <h3 className="">{props.children}</h3>;

                    case "h4":
                        return <h4 className="">{props.children}</h4>;
                    case "li":
                        return <h4 className="">{props.children}</h4>;

                    case "blockquote":
                        return (
                            <blockquote className="">
                                {props.children}
                            </blockquote>
                        );

                    case "p":
                        return <p className="p-2">{props.children}</p>;

                    default:
                        return props.children;
                }
            }
        }
    };

    return (
        <BlockContent
            serializers={serializers}
            blocks={body}
            imageOptions={{ w: 320, h: 240, fit: "max" }}
            {...client.config()}
        />
    );
}
