import Head from "next/head";
import { getAllCategories } from "../content-api";
import imageBuilder from "../image-builder";

export async function getStaticProps() {
    const data = await getAllCategories();

    return {
        props: {
            categories: data
        }
    };
}

export default function Index(props) {
    const { categories } = props;
    const showImage = src => imageBuilder(src).url();

    const goTo = url => (window.location.href = url);

    return (
        <>
                <div className="mx-auto container pt-5">
                    {categories.map(category => (
                        <div
                            onClick={() =>
                                goTo(`/category/${category.slug.current}`)
                            }
                            className="float-left bg-gray-700 w-64 h-64 m-2 relative cursor-pointer"
                        >
                            <img
                                className="h-full w-full"
                                src={showImage(category.image)}
                            />
                            <div className="bg-gray-300 w-64 z-10 h-10 p-2 absolute bottom-0 hover:font-light">
                                <a href={`/category/${category.slug.current}`}>
                                    {category.title}
                                </a>
                            </div>
                        </div>
                    ))}
                </div>
        </>
    );
}
