import imageUrlBuilder from "@sanity/image-url";
import BlockContent from "@sanity/block-content-to-react";
import client from "../../sanity-client";
import getPostBySlug from "../../content-api";
import moment from 'moment'

function urlFor(source) {
  return imageUrlBuilder(client).image(source);
}

export async function getServerSideProps(context) {
  const { slug = "" } = context.query;
  const data = await getPostBySlug(slug);

  return {
    props: data,
  };
}

const serializers = {
  types: {
    code: props => (
      <pre data-language={props.node.language}>
        <code>{props.node.code}</code>
      </pre>
    )
  }
}

export default function Post(props) {
  const {
    title,
    name,
    authorImage,
    publishedAt,
    body = [],
  } = props;
  return (
    <article className="p-8">
      <h3 className="font-bold text-gray-500">{ moment(publishedAt).format('MMMM Do, YYYY') }</h3>
      <h1 className="text-4xl font-bold text-gray-900">{title}</h1>
      <div className="text-xl text-gray-800">
        <BlockContent
        blocks={body}
        imageOptions={{ w: 320, h: 240, fit: "max" }}
        {...client.config()}
        />
      </div>
      {authorImage && (
        <div>
          <img className="h-12 rounded shadow-xl" alt="The author" src={urlFor(authorImage).width(100).url()} />
      <span className="text-center">By {name}</span>
        </div>
      )}
    </article>
  );
};
