import imageUrlBuilder from "@sanity/image-url";
import client from "../../sanity-client";
import getPostBySlug from "../../content-api";
import moment from "moment";
import renderBodyContent from "../../render-body-content";

function urlFor(source) {
    return imageUrlBuilder(client).image(source);
}

export async function getServerSideProps(context) {
    const { slug = "" } = context.query;
    const data = await getPostBySlug(slug);

    return {
        props: data
    };
}

export default function Post(props) {
    const { title, publishedAt, body = [] } = props;
    return (
        <>
            <nav class="flex items-center justify-between flex-wrap bg-gray-700 p-6">
                <div class="flex items-center flex-shrink-0 text-white mr-6">
                    <span class="font-semibold text-xl tracking-tight">
                        Fairchild Portfolio & Projects
                    </span>
                </div>
                <div class="block lg:hidden">
                    <button class="flex items-center px-3 py-2 border rounded text-teal-200 border-teal-400 hover:text-white hover:border-white">
                        <svg
                            class="fill-current h-3 w-3"
                            viewBox="0 0 20 20"
                            xmlns="http://www.w3.org/2000/svg"
                        >
                            <title>Menu</title>
                            <path d="M0 3h20v2H0V3zm0 6h20v2H0V9zm0 6h20v2H0v-2z" />
                        </svg>
                    </button>
                </div>
                <div class="w-full block flex-grow lg:flex lg:items-center lg:w-auto">
                    <div class="text-sm lg:flex-grow">
                        <a
                            href="#responsive-header"
                            class="block mt-4 lg:inline-block lg:mt-0 text-white hover:text-white mr-4"
                        >
                            Docs
                        </a>
                        <a
                            href="#responsive-header"
                            class="block mt-4 lg:inline-block lg:mt-0 text-white hover:text-white mr-4"
                        >
                            Examples
                        </a>
                        <a
                            href="#responsive-header"
                            class="block mt-4 lg:inline-block lg:mt-0 text-teal-200 hover:text-white"
                        >
                            Blog
                        </a>
                    </div>
                    <div>
                        <a
                            href="#"
                            class="inline-block text-sm px-4 py-2 leading-none border rounded text-white border-white hover:border-transparent hover:text-teal-500 hover:bg-white mt-4 lg:mt-0"
                        >
                            Download
                        </a>
                    </div>
                </div>
            </nav>{" "}
            <article className="bg-gray-200 p-8 mx-auto max-w-3xl shadow-md">
                <h3 className="font-bold text-gray-500">
                    {moment(publishedAt).format("MMMM Do, YYYY")}
                </h3>
                <h1 className="text-4xl font-bold text-gray-800">{title}</h1>
                <div className="text-xl text-gray-700">
                    {renderBodyContent(body)}
                </div>
            </article>
        </>
    );
}
