import Head from "next/head";
import { getAllCategories } from "../content-api";
import imageBuilder from "../image-builder";

export async function getStaticProps() {
    const data = await getAllCategories();

    return {
        props: {
            categories: data
        }
    };
}

export default function Index(props) {
    const { categories } = props;
    const showImage = src => imageBuilder(src).height(400).width(400).url();

    const goTo = url => (window.location.href = url);

    return (
        <>
            {categories.map(category => (
                <div className="border-solid border-4 border-gray-600 sm:w-full sm:h-full md:w-64 md:h-64 m-2">
                    <div
                        onClick={() =>
                            goTo(`/category/${category.slug.current}`)
                        }
                        className="float-left cursor-pointer"
                    >
                        <img
                            className="object-scale-down h-full w-full"
                            src={showImage(category.image)}
                        />
                    </div>
                </div>
            ))}
        </>
    );
}
